let htmlTemplate = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <link rel="stylesheet" type="text/css" href="/net/splitcells/website/css/jquery-fancytree-all-deps.min.css">
</head>
<body></body>
</html>`;
$(".net-splitcells-website-tree-interactive").each((index, tree) => {
    /* Replaces tree elements by interactive versions based on Fancytree.
     * The interactive tree is contained inside an iframe,
     * in order to avoid CSS conflicts of Fancytree and the website's CSS files.
     *
     * TODO For some reason this causes an endless loading time in the browser.
     * It is not known, what is causing this and it also does not seem to create a big CPU utilization or
     * other problems.
     */
    tree.innerHTML = '<iframe style="width: 100%; height: 100%;"></iframe>';
    tree.childNodes[0].contentWindow.document.write(htmlTemplate);
    $(tree.childNodes[0].contentWindow.document.body).fancytree({
            source: {
                url: tree.getAttribute('source-path')
                , cache: false
                }
            });
    });